import React, {Component} from "react";
import axios from "axios";

const Item = props => (
    <div>
      <h4>{props.item.itemname}</h4>
      <table className="table">
        <tr>
            <td>{props.item.description}</td>
            <td className="right">{props.item.price}</td>
        </tr>
      </table>

    </div>
)

