import React, {Component} from "react";
import axios from "axios";

export default class EditItems extends Component{
    constructor(props){
        super(props);

        this.onChangeItemname = this.onChangeItemname.bind(this);
        this.onChangeDescription = this.onChangeDescription.bind(this);
        this.onChangePrice = this.onChangePrice.bind(this);
        this.onChangeShow = this.onChangeShow.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            itemname: '',
            description: '',
            price: 0,
            show: ''
        }
    }

    componentDidMount(){
        const params = new URLSearchParams(window.location.search)

        axios.get('http://localhost:3001/items/'+this)
            .then(response => {
                this.setState({
                    itemname: response.data.itemname,
                    description: response.data.description,
                    price: response.data.price,
                    show: response.data.show
                })   
            })
            .catch(function (error) {
                console.log(error);
            })
    }

    onChangeItemname(e){
        this.setState({
            itemname: e.target.value
        });
    }

    onChangeDescription(e){
        this.setState({
            description: e.target.value
        });
    }
    onChangePrice(e){
        this.setState({
            price: e.target.value
        });
    }
    onChangeShow(e){
        this.setState({
            show: e.target.value
        });
    }

    onSubmit(e){
        e.preventDefault();
        const item = {
            itemname: this.state.itemname,
            description: this.state.description,
            price: this.state.price,
            show: this.state.show,
        }
        console.log(item);

        axios.post('http://localhost:3001/items/update/'+this.props.match.params.id, item)
            .then(res => console.log(res.data));

        window.location = "/menu-add";
    }

    render(){
        return(
            <div>
                <h3>Edit Menu Item</h3>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>Menu Item Name: </label>
                        <input type="text" 
                            required
                            className="form-control"
                            value={this.state.itemname}
                            onChange={this.onChangeItemname}
                        />
                    </div>
                    <div className="form-group">
                        <label>Description: </label>
                        <input type="text" 
                            required
                            className="form-control"
                            value={this.state.description}
                            onChange={this.onChangeDescription}
                        />
                    </div>
                    <div className="form-group">
                        <label>Price: </label>
                        <input type="text"
                            required
                            className="form-control"
                            value={this.state.price}
                            onChange={this.onChangePrice}
                        />
                    </div>
                    <div className="form-group">
                        <label>Show: </label>
                        <select value={this.state.value} onChange={this.onChangeShow}>
                            <option value="true">true</option>
                            <option value="false">false</option>
                        </select>
                    </div>

                    <div className="form-group">
                        <input type="submit" value="Edit Menu Item" className="btn btn-primary" />
                    </div>
                </form>
            </div>
        )
    }
}
